        int left = 1;
        int right = nums.length - left;
        long[] arr = new long[nums.length];
        while(i < nums.length){
            long diff = Math.abs((suml/left) - (sumr/right));
            arr[calc] = diff;
            if(diff < min){
                min = diff;
                idx = calc;
