        arr[nums.size()-1] /= nums.size(); //Last index will have no elements on right side so it will store average of all.

        long long int &temp = *min_element(arr.begin(),arr.end());// to find the smallest element in array with least average difference.

        for(int i=0;i<nums.size();i++)
        {
            if(arr[i]==temp)return i;//returning index of smallest element.
        }
        return 0;
    }
};
