class Solution {
public:

    int minimumAverageDifference(vector<int>& nums) 
    {    
        int ans_idx=0;
        int n=nums.size();
        if(n==1) return 0;

        long long ans = INT_MAX, sum = 0;

        for(int i=0;i<n;i++)
            sum += nums[i];
        // prefix sum
        vector<long long>pre(n);

        pre[0] = nums[0];
        for(int i=1;i<n;i++)
            pre[i] = nums[i] + pre[i-1];

        for(int i=0;i<nums.size();i++)
        {    
            long long firstSum = pre[i];
            long long lastSum = (sum - pre[i]);
            long long diff=0;

            if((n-i-1)!= 0)
                diff = abs(firstSum/(i+1) - lastSum/(n-i-1));
            else
                diff = firstSum/(i+1) - 0;

            if(diff < ans)
            {
                ans = diff;
                ans_idx = i;
            }
        }
        return ans_idx;
    }
};
