class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        long long minAv = INT_MAX;
        int idx = 0;
        long long sSum = 0, eSum = accumulate(nums.begin(),nums.end(),(long long)0);
        long long p=0,q=nums.size();
        for(int i=0;i<nums.size();i++){
            sSum += nums[i];
            eSum -= nums[i];
            p++, q--;
            long long av1 = sSum / p;
            long long av2;
            if(q==0)
                av2 = 0;
            else
                av2 = eSum / q;;
            if(abs(av1-av2) < minAv){
                minAv = abs(av1-av2);
                idx = i;
            }
        }
        return idx;
    }
};
