class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        int ans=INT_MAX;
        long long total=0;  
        int n=nums.size();  //size of vector

        for(int i=0;i<n;i++){
            total+=nums[i];    //total sum of all elements
        }

        long long curr=0;
        int ind=-1;
        for(int i=0;i<n-1;i++){
            curr+=nums[i];     //sum of elements upto current index i
            long long last=total-curr;      //sum of remaining elements
            long long p=abs((curr/(i+1))-(last/(n-i-1)));        //average 
