class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        vector<long> prefixSum(nums.size(), 0);

        long sum = 0;

        // store the accumulated sum at the index 
        for (int i = 0; i < nums.size(); i ++) {
            sum += nums[i];
            prefixSum[i] = sum;
        }

          vector<pair<long,int>> vec; // set up a vec to store pairs with the first one as the res and the second one as the index

        for (int i = 0; i < nums.size(); i++) {
            long firstPartSum = prefixSum[i];
            long secondPartSum = prefixSum[nums.size()-1] - firstPartSum; 
            // the second part sum is just the last item from the vec subtract the firstPart sum
            long firstPartSumAverage = firstPartSum/ (i+1);
            long secondPartSumAverage = 0;
            if (secondPartSum != 0) {
                secondPartSumAverage = secondPartSum/(nums.size() - (i+1));
            }
            long res = abs(firstPartSumAverage - secondPartSumAverage);
            vec.push_back({res, i});
            // once we have the result store the result as a pair of {res, index} to vec
        }

        sort(vec.begin(), vec.end()); // sort the vec from smallest to biggest

        return vec[0].second; // just return the first item's index from the vec
    }
};
