class Solution {
    public int minimumAverageDifference(int[] nums) {
        int n = nums.length;
        if (n == 1) return 0;

        long tot = 0;
        for(int i : nums) 
            tot += i;

        long left = 0;
        long minDiff = Integer.MAX_VALUE;
        int ans = -1;
        for (int i = 0; i < n; ++i) {
            left += nums[i];
            long right = tot - left;  
            long diff = Math.abs((left / (i + 1)) - 
                ((n - i == 1) ? 0: right / (n - i - 1)));

            if (diff < minDiff) {
                minDiff = diff;
                ans = i;
            }
        }
        return ans;
    }
}
