class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        // size of nums vector
        int n = nums.size();

        // data-structure to store prefix-sum
        vector<long long> prefixsum(n,0);

        // initializing prefix sum vector
        prefixsum[0] = nums[0];


        // storing prefix-sum
        for(int i=1;i<n;i++){
            prefixsum[i] = nums[i] + prefixsum[i-1];
        }

        // variable keeping track of minimum average differnece
        int res = INT_MAX;

        // variable to return index of minimum average differnece
        int resindex = 0;

        // I declared this two variables to obtain the sum of last n-i-1 elements from last
        int a,b;

        // Find answer manually by going through all indics
        for(int i=0;i<n;i++){
            if(i == n-1)
                a = 0;
            else
                a = (prefixsum[n-1]-prefixsum[i])/(n-i-1) ;
            b = (prefixsum[i]/(i+1));
            int diff = a - b;

            if(diff < 0) diff *= -1;

            if(diff < res){
                resindex = i;
                res = min(res,diff);
            }
        }

        // Return the index of our answer
        return resindex;
    }
};
