public class Solution {
    public int MinimumAverageDifference(int[] nums) 
    {
        int count = nums.Length;
        if(count == 1)
            return 0;
        long leftSum = 0;
        long totalsum = nums.Sum(x=>(long)x);
        for(int i =0; i< count; i++)
        {
            leftSum += nums[i];
            if(i != nums.Length -1)
            {
                nums[i] = Convert.ToInt32(Math.Abs((leftSum / (i + 1)) - (totalsum - leftSum) / (nums.Length - (i + 1))));
                if(nums[i]  == 0)
                {
                    return i;
                }
            }
            else
            {
                nums[i] = Convert.ToInt32(Math.Abs(leftSum /(i+1)));
            }
        }

        long storemin = nums.Min();
        return Array.FindIndex(nums, x => x == storemin);
    }
}
```

