class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        int n = nums.size();
        vector<long long int> pre(n);
        pre[0] = nums[0];
        for(int i=1;i<n;i++) {
            pre[i] = nums[i] + pre[i-1];
        }
        long long int mini = INT_MAX , ans = 0,tot = pre[n-1];
        for(int i=0;i<n;i++) {
            long long int f = pre[i]/(i+1);
            long long int s = 0;
            if(i != (n-1)) s = (tot - pre[i])/(n-i-1);

            long long int diff = abs(f-s);
            if(diff < mini) {
                ans = i;
                mini = diff;
            }
        }
        return ans;
    }
};
