class Solution {
public:
    long min1(long a,long b)
    {
        return a>b?b:a;
    }
    int minimumAverageDifference(vector<int>& nums) {
        int mn=INT_MAX;
        int n=nums.size();
        vector<int> v;
        vector<long> sm(n+1,0);
        sm[0]=nums[0];
        for(int i=1;i<nums.size();i++)
            sm[i]=sm[i-1]+nums[i];
        for(int i=0;i<n;i++){
            if(i!=n-1)
                mn=min1(mn,abs(sm[i]/(i+1)-(sm[n-1]-sm[i])/(n-i-1)));
            else
                mn=min1(mn,sm[n-1]/n);
            v.push_back(mn);
        }
        for(int i=0;i<nums.size();i++){
            if(v[i]==mn)
                return i;
        }
        return -1;
    }
};
