    nums.forEach((data, index)=> {
        leftTotal += data;
        rightTotal -= data;
        let currentAverageDiff = Math.abs(Math.floor(leftTotal/(index+1)) - Math.floor(rightTotal/(numLength-index-1) || 0));
        if (currentAverageDiff < mins) {
            resultIndex = index;
            mins = currentAverageDiff;
        }
    });
    return resultIndex;
};

