#define ll long long
class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        ll n = nums.size();
        vector<ll> presum;
        vector<ll> postsum;

        ll curr_sum = 0;
        for(ll i=0;i<nums.size();i++){
            curr_sum += nums[i];
            presum.push_back(curr_sum);
