class Solution {
    public int minimumAverageDifference(int[] nums) {
        long n = nums.length, prefixSum[] = new long[(int)n], tot = 0, res = -1, minAvgDiff = Long.MAX_VALUE, avgDiff, postfixSum;
        int i;
        for(i = 0; i<n; i++) prefixSum[i] = (tot += nums[i]);
        for(i = 0; i<n; i++) {
            postfixSum = tot==prefixSum[i]? 0: (tot - prefixSum[i])/(n - i - 1);
            avgDiff = (int)Math.abs(prefixSum[i]/(i + 1) - postfixSum);
            if(avgDiff < minAvgDiff) {
                minAvgDiff = avgDiff;
                res = i;
            }
        }
        return (int)res;
    }
}
