class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        int N = nums.size(), idx=-1;
        if ( N < 2) return 0;
        long endSum=0, begSum=0, min_avg = INT_MAX,avg;
        for(auto e: nums) endSum += e;

        for (int i=0; i < N; i++){
            begSum += nums[i]; //sum 0 to i
            endSum -= nums[i]; //sum i+1 to N-1

            if (i != N-1)avg = begSum/(i+1) -endSum/(N-i-1);
            else avg = begSum/(i+1); // Avoid divide by 0
            if ( abs(avg) < min_avg){
                min_avg = abs(avg); //Track minmum and index
                idx = i;
            }
        }

        return idx;
    }
};
