class Solution 
{
public:
    int minimumAverageDifference(vector<int>& nums) 
    {
        if (nums.size() == 1) {
            return 0;
        }

		// Storing prefix sum
        vector<long long> sums;
        for (auto num : nums) {
            if(sums.size() == 0) {
                sums.push_back(num);
            }
            else {
                sums.push_back(sums.back() + num);
            }
        }

        long long idx = 0;
        long long f = nums[0], b = (sums.back() - nums[0]) / (sums.size() - 1), S = abs(b - f);

        for (long long i = 1; i < sums.size() - 1; i++) 
        {
            f = sums[i] / (i + 1);
            b = (sums.back() - sums[i]) / (sums.size() - (i + 1));
            if (abs(f - b) < S) 
            {
                S = abs(f - b);
                idx = i;
            }
        }

        if (sums.back() / sums.size() < S) 
        {
            idx = sums.size() - 1;
        }

        return idx;                     
    }
};
