class Solution {
public:
//prefix array based question
    int minimumAverageDifference(vector<int>& nums) {
        vector<long long>pre(nums.size(),0);
        pre[0]=nums[0];
        for(long long i=1;i<nums.size();i++)
        {
            pre[i]=pre[i-1]+nums[i];
        }
        long long ans=LONG_MAX;
        int index=0;
        long long sum=accumulate(nums.begin(),nums.end(),0LL);//0LL as we want it in long long 
        //sum -pre[i] will work as suffix array
        for(long long i=0;i<nums.size();i++)
        {
            long long left=0,right=0;
             left=pre[i]/(i+1);
             if(nums.size()-1-i>0)//to prevent from divide by zero 
             right=(sum-pre[i])/(nums.size()-1-i);
            // cout<<left<<" "<<right<<endl;
            long long vali=abs(left-right);
           if(ans>vali)
           {
               ans=vali;
               index=i;
           }
        }
        return index;
    }
};
