class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {

        int n = nums.size();

        // calculate total sum of nums

        long long total_sum = 0;

        for(int i = 0; i < n; i++)
        {
            total_sum += nums[i];
        }

        long long curr_sum = 0;

        long long mini = INT_MAX;

        int min_idx = 0;

        for(int i = 0; i < n; i++)
        {
            curr_sum += nums[i];

            // calculate average of first (i + 1) elements

            long long left_avg = curr_sum / (i + 1);

            // claculate average of last (n - i - 1) elements

            long long right_avg = 0;

            // there should be atleast one element on right side

            if(n - i - 1 > 0)
            {
                right_avg = (total_sum - curr_sum) / (n - i - 1); 
            }

            // find the absolute diff. of left part and right part

            // then update the result

            if(abs(left_avg - right_avg) < mini)
            {
                mini = abs(left_avg - right_avg);

                min_idx = i;
            }
        }

        return min_idx;
    }
};
