class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
        int n=nums.size();
        vector<long> pre(n);
        pre[0]=nums[0];
        for(int i=1;i<n;i++){
            pre[i]=pre[i-1]+nums[i];
        }
        vector<pair<long,long>> vp;
        for(int i=0;i<n-1;i++){
            long r1=pre[i]/(i+1);
            long r2=(pre[n-1]-pre[i])/(n-i-1);
            long res=abs(r1-r2);
            vp.push_back({res,i});
        }
        vp.push_back({pre[n-1]/n,n-1});
        sort(vp.begin(),vp.end());
        return vp[0].second;
    }
};
