```
class Solution {
    func minimumAverageDifference(_ nums: [Int]) -> Int {

        // Result for 0 or 1 element array will always be 0
        guard nums.count > 1 else { return 0 }

        // Total value of all elements of nums added up
        let total = nums.reduce(0, +)
        var left = 0
        var right = total

        // Will be nil until first iteration
        var minimum: (Int, Int)?

        for i in 0..<nums.count-1 {
            left += nums[i]
            right -= nums[i]
            let avgDiff = abs(left/(i+1) - right/(nums.count-i-1))
            if minimum != nil, avgDiff < minimum!.1 {
                minimum = (i, avgDiff)
            } else if minimum == nil {
                minimum = (i, avgDiff)
            }
        }

        // Last iteration separated due to divide by 0
        let avgDiff = abs(total/nums.count)
        if avgDiff < minimum!.1 {
            minimum = (nums.count-1, avgDiff)
        }

        return minimum!.0
    }
}
```

