    public int minimumAverageDifference(int[] nums)
    {
		int sum = 0, temp = 0, result = 999999, result2= 0; 
        Integer[]  a = new Integer[nums.length];


        for (int i=0; i < nums.length ; i++) sum += nums[i];

        for (int i = 0; i < nums.length-1; i++) {
            temp += nums[i];
            sum -= nums[i];
            a[i] = Math.abs(temp / (i+1) - sum / ((nums.length)-(i+1))) ;

            if(a[i]<result)
            {
                result = a[i];
                result2 = i;
            }
        }


        temp += nums[(nums.length-1)];
        a[(nums.length-1)] = Math.abs((temp / (nums.length))) ;
        if((a[(nums.length-1)])<result)return (nums.length-1);

        return result2;
    }
}


