class Solution {
public:
    int minimumAverageDifference(vector<int>& nums) {
       int n = nums.size();
       if(n==1)
       return 0;
       long sum =0;
       long long h =0;
       long long c=0;
       long long p=0;
       long long result = INT_MAX;
       for(int i=0;i<n;i++)
           c= c+ nums[i];
       for(int i=0;i<n-1;i++)
       {
          h = nums[i]+h;
          int k = c-h;
          sum = abs((h/(i+1))-(k/(n-i-1)));
          if(sum<result)
             {
                  p = i;
                  result = sum;
             }

       }  
        sum = c/n;
        if(sum<result)
          return n-1;
       return p;
    }
};
