int sumi(int ind, vector<vector<int>> &piles, int k, int n){
    if(ind==n or k==0) return 0;
    int notPick=sumi(ind+1,piles,k,n);
    int pick=0,coins=k,sum=0;
    for(int i=0;i<piles[ind].size() and coins>0;i++){
        sum+=piles[ind][i];
