class Solution {
    public int helper(List<List<Integer>> piles,int idx,int k,int [][]dp){

        if(idx<0 || k == 0) return 0;
        if(dp[idx][k] != -1) return dp[idx][k];


        // exclude case 
        int exclude = helper(piles,idx-1,k,dp);


        // include case
        int sum = 0;
        int include = 0;

        List<Integer> curr_pile = piles.get(idx);

        for(int i=0;i<Math.min(curr_pile.size(),k);i++){
            sum += curr_pile.get(i);
            include = Math.max(sum + helper(piles,idx-1,k-i-1,dp),include);
        }
        return dp[idx][k] = Math.max(include,exclude);
    }
    public int maxValueOfCoins(List<List<Integer>> piles, int k) {

        int n = piles.size();
        int [][]dp = new int[n+1][k+1];
        for(int []d:dp) Arrays.fill(d,-1);

        return helper(piles,n-1,k,dp);
    }
}

