class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& P, int k) {
        int m = P.size();
        for(int i = 0 ; i< m ; i++){
            int n = P[i].size();
            for(int j = 1 ; j<n; ++j){
                P[i][j] += P[i][j-1];
            }
        }// sum of value if we take j elements from top of any pile of coins.
        vector<int> mxc(k+1,0);
        for(int j = min(k,(int)P[0].size()) ; j>0; --j){
            mxc[j] = P[0][j-1];
        }
        for(int i = 1 ; i< m ; i++){
            int n = P[i].size();
            for(int j=k; j>0 ; --j){//Final Sum
                for(int l=min(j,n); l>0; --l){
                    mxc[j] = max(mxc[j-l]+P[i][l-1],mxc[j]);
                }

            }
        }
        return mxc[k];

    }
};
