class Solution {
public:
    long long dp[2005][2005];

    int K;
    long long MaxValue(vector<vector<int>>& piles, int taken,int pile)
    {
        if(taken==K) return 0;
        if(pile>=piles.size()||taken>K) return INT_MIN;

        if(dp[taken][pile]!=-1) return dp[taken][pile];

        long long ans=0;
		// if we want to pick and try atleast one coin from this pile

        for(int i=0;i<piles[pile].size();i++)
        {
            ans=max(ans,piles[pile][i]+MaxValue(piles,taken+i+1,pile+1));
        }

        ans=max(ans,MaxValue(piles,taken,pile+1)); // if we want to skip this pile altogether

        return dp[taken][pile]=ans;
    }

    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        memset(dp,-1,sizeof(dp));

		// storing the prefix sum so as to know if we pick the ith coin out of a pile, what will be the total sum of all the coins above the ith coin 

        for(int i=0;i<piles.size();i++)
        {
            for(int j=1;j<piles[i].size();j++) piles[i][j]+=piles[i][j-1];
        }

        K=k;
        return MaxValue(piles,0,0);
    }
};
