class Solution
{

    /**
     * @param Integer[][] $piles
     * @param Integer $k
     * @return Integer
     */
    function maxValueOfCoins($piles, $k)
    {
        $a = array_fill(0, 2, array_fill(1, $k, 0));
        $c = 0;
        foreach ($piles as $pile) {
            for ($i = 1; $i <= $k; $i++) {
                for ($j = 0, $s = 0, $kk = min($i, count($pile)) ; $j < $kk; $j++) {
                    $s += $pile[$j];
                    $a[$c][$i] = max($a[$c][$i], $a[1 - $c][$i], $s + $a[1 - $c][$i - $j - 1]);
                }
            }
            $c = 1 - $c;
        }

        return end($a[1 - $c]);
    }
}

