# Top Down DP linear space
```
class Solution {

public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {

        int n = piles.size();
        vector<int> dp(k + 1);
        dp[0] = 0;

        for(int j = 1; j <= k; j++)
            dp[j] = dp[j - 1] + (j - 1 < piles[n - 1].size() ? piles[n - 1][j - 1] : 0);

        for(int i = n - 2; i >= 0; i--)
        {
            vector<int> _dp(k + 1);
            _dp[0] = 0;
            for(int j = 1; j <= k; j++)
            {
                int preSum = 0;
                int currMax = dp[j];
                for(int x = 1; x <= j && x - 1 < piles[i].size(); x++)
                {
                    preSum += piles[i][x - 1];
                    currMax = max(currMax, preSum + dp[j - x]);
                }
                _dp[j] = currMax;
            }
            dp.swap(_dp);
        }

        return dp[k];
    }
};

```


# Bottom Up DP
```

class Solution {

    int DFS(int index, int k, vector<vector<int>>& piles, vector<vector<int>> &dp)
    {
        if(index >= piles.size() || k == 0)
            return 0;

        if(dp[index][k] != -1)
            return dp[index][k];

        int ret = 0;
        int preSum = 0;

        // completely ignore the current pile
        ret = DFS(index + 1, k, piles, dp);

        // try to add more and more coins from current pile and then move on
        for(int i = 0; i < piles[index].size() && k - i - 1 >= 0; i++)
        {
            preSum += piles[index][i];
            ret = max(ret, preSum + DFS(index + 1, k - i - 1, piles, dp));
        }

        return dp[index][k] = ret;
    }

public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {

        vector<vector<int>> dp(piles.size(), vector<int>(k + 1, -1));
        return DFS(0, k, piles, dp);
    }
};
```

