class Solution {
private:
    int solve(int ind, vector<vector<int>>& piles, vector<vector<int>>& dp, int k) {
        if(ind >= piles.size()) return 0;

        if(dp[ind][k] != -1) return dp[ind][k];

        int sum = 0, take = 0;

        //not taking from current pile;
        int notTake = 0 + solve(ind+1,piles,dp,k);

        //taking from current pile if we can take according to given k and move towards next ind
        for(int i=0;i<piles[ind].size();i++) {
            sum += piles[ind][i];

            if(k - (i+1) >= 0) {
                take = max(take, sum + solve(ind+1, piles, dp, k-(i+1)));
            }
        }

        return dp[ind][k] = max(take, notTake);
    }

public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n = piles.size();
        vector<vector<int>> dp(n+1, vector<int>(k+1, -1));
        return solve(0,piles,dp,k);
    }
};
