```
public class Solution {
    public int MaxValueOfCoins(IList<IList<int>> piles, int k) {
        var rs = MaxValueOfCoins((0, k), new Dictionary<(int index, int k), int>(), piles);
        return rs;
    }
    private int MaxValueOfCoins((int index, int k) key, Dictionary<(int index, int k), int> dic, IList<IList<int>> piles)
    {
        if (piles.Count <= key.index || key.k <= 0) return 0;
        if (dic.ContainsKey(key)) return dic[key];
        var rs = MaxValueOfCoins((key.index + 1, key.k), dic, piles);
        var sum = 0;
        for (int i = 0; i < Math.Min(piles[key.index].Count, key.k); i++)
        {
            sum += piles[key.index][i];
            var rs0 = sum + MaxValueOfCoins((key.index + 1, key.k - (i + 1)), dic, piles);
            if (rs < rs0) rs = rs0;
        }
        if (!dic.ContainsKey(key)) dic.Add(key, rs);
        return rs;
    }
}
