    int f(vector<vector<int>>& piles, int n, int i, int k, 
    vector<vector<int>>& dp) {
        if (i == n) {
            if (!k) return 0;
            return -1e9;
        }
        if (dp[i][k] != -1) return dp[i][k];
        int maxVal = -1e9, currVal = 0;
        for (int j = 0; j <= piles[i].size(); j++){
            if (j == 0) maxVal = max(maxVal, f(piles, n, i + 1, k, dp));
            else if (k - j >= 0){
                currVal += piles[i][j - 1];
                maxVal = max(maxVal, currVal + f(piles, n, i + 1, 
                k - j, dp));
            }
        }
        return dp[i][k] = maxVal;
    }

    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n = piles.size();
        vector<vector<int>> dp(n, vector<int>(k + 1, - 1));
        return f(piles, n, 0, k, dp);
    }
};
