        int n = piles.size();
        vector<vector<int>> dp(n + 1, vector<int>(k + 1, -1e9));
        dp[n][0] = 0;

        for (int i = n - 1; i >= 0; i--) {
            for (int K = 0; K <= k; K++) {
                int maxVal = -1e9, currVal = 0;
                for (int j = 0; j <= piles[i].size(); j++){
                    if (j == 0) 
                        maxVal = max(maxVal, dp[i + 1][K]);
                    else if (K - j >= 0){
                        currVal += piles[i][j - 1];
                        maxVal = max(maxVal, currVal + dp[i + 1][K - j]);
                    }
                }
                dp[i][K] = maxVal;
            }
        }
        return dp[0][k];
    }
};
