class Solution {
    public int maxValueOfCoins(List<List<Integer>> piles, int k) {
        return dfs(piles, 0, k);
    }

    private int dfs(List<List<Integer>> piles, int i, int coins) {
       if (i == piles.size())
           return 0;

        int res = dfs(piles, i + 1, coins); // skip curr pile

        int currPile = 0; // get 1 to k coins from curr pile

        for (int j = 0; j < Math.min(piles.get(i).size(), coins); j++) {
            currPile += piles.get(i).get(j);
            res = Math.max(res, currPile + dfs(piles, i + 1, coins - j - 1));
        }

        return res;
    }
}
