class Solution {
public:
    int lol(int ind, int k, int n, vector<vector<int>> &piles, vector<vector<int>> &dp){
        //base case
        //if we reach the end of pile traversal
        //or if we reach the max limit of coins chosen
        if(ind==n or k==0) return 0;
        if(dp[ind][k]!=-1) return dp[ind][k];

        int maxi=INT_MIN;
        //dont pick from that pile, move to next
        int notpick= lol(ind+1,k,n,piles,dp);
        //ctc is variable holding the number of coins 
        //we have left to choose
        int pick=0, ctc=k, sum=0;

        for(int i=0;i<piles[ind].size() and ctc>0;i++){
            ctc--;
            sum+=piles[ind][i];
            int r= sum + lol(ind+1,ctc,n,piles,dp);
            pick=max(pick,r);
