class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n=piles.size();
        int dp[n+1][k+1];
        for(int i=0;i<=n;i++)
        {
            for(int j=0;j<=k;j++)
            {
                if(i==0 || j==0) // i = 0 means no items are available and j = 0 means no items are chosen so maximum value = 0 
                {
                    dp[i][j]=0;
                    continue;
                }
                int s = 0;
                dp[i][j]=dp[i-1][j]; // no items are chosen from i th pile so maximum value is same as chosing j items from (i-1) th pile
                for(int k1=1;k1<=min(j,(int)piles[i-1].size());k1++)  // chosing k1 items from i th pile so j - k1 items will be selected from (i-1) th pile 
                {
                    s+=piles[i-1][k1-1];
                    dp[i][j] = max(dp[i][j],dp[i-1][j-k1] + s);
                }
            }
        }
        return dp[n][k];  // final answer chosing k items from n piles
    }
};

