    int maxValueOfCoinsUtils(vector<vector<int>> &piles, int n, int k, vector<vector<int>> & dp)
    {
        if(k == 0 || n == 0)
        {
            return 0;
        }

        if(dp[n-1][k] != -1)
        {
            return dp[n-1][k];
        }

        /*
         * Selected a Pile
         */
        int sum = 0;
        int n_selected_pile = 0;
        for(int i = 0; i < piles[n-1].size() && i < k; i++)
        {
            sum += piles[n-1][i];
            n_selected_pile = max(n_selected_pile, sum + maxValueOfCoinsUtils(piles, n - 1, k - i - 1, dp));
        }

        /*
         * Not -selected a Pile
         */
        int n_not_selected_pile = maxValueOfCoinsUtils(piles, n - 1, k, dp);

        return dp[n-1][k] = max(n_selected_pile, n_not_selected_pile);

    }
