    int fun(int i,int k,vector<vector<int>>& piles,int n)
    {
        if(i>=n || k<0){return 0;}
        if(dp[i][k]!=-1){return dp[i][k];}
        int ans=fun(i+1,k,piles,n);//first choice ,to skip the current stack
        for(int j=0;j<piles[i].size();j++)//either u want top coin or top 2 coins or top 3....and so on
        {     if(k-(j+1)<0){break;} //it is not possible to take more than k coins,,
            int temp=piles[i][j]+fun(i+1,k-(j+1),piles,n);
            if(temp>ans){ans=temp;}
        }
        return dp[i][k]=ans;

