class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n = piles.size();
        vector<vector<int>> ps(n); //Calculating all the prefix sums

        for(int i=0;i<n;i++){
            int sz = piles[i].size();

            ps[i]=vector<int>(sz+1,0);

            for(int j=0;j<sz;j++)
                ps[i][j+1]=ps[i][j]+piles[i][j];

        }

        int dp[n+1][k+1];
        memset(dp,0,sizeof(dp));
        /*Here dp[i][j] represents the max value obtained after picking up j coins up to the i th pile.
		   Therefore, dp[i][j] = max(dp[i-1][x] + ps[i-1][j-x]) i.e. max of picking j-x coins upto  i-1 th pile
		   and then picking the remaining x coins in the ith pile.Here  x can go from 0 to min(k,piles[i-1].size()).
		*/
        for(int i=1;i<=n;i++){
            for(int j=1;j<=k;j++){
                int lim = min(j,(int)piles[i-1].size());
                for(int x=0;x<=lim;x++)
                    dp[i][j] = max(dp[i-1][j-x] + ps[i-1][x],dp[i][j]);
            }
        }
        return  dp[n][k];
    }
};
