class Solution {
    public int maxValueOfCoins(List<List<Integer>> piles, int k) {
        int numPiles = piles.size();

        int[][] dp = new int[numPiles+1][k+1];

        for(int i=1; i<=numPiles; i++){
            for(int j=1; j<=k; j++){
                dp[i][j] = dp[i-1][j]; // Not taking any coin at all from the current pile

                // For the current pile, we have the option to take the atmost j coins if present
                List<Integer> currPile = piles.get(i-1);
                int coinSum = 0;
                for(int c=0; c < Math.min(currPile.size(), j); c++){
                    coinSum += currPile.get(c);

                    // Take c coins from the current pile and remaining coins from the previous piles
                    dp[i][j] = Math.max(dp[i][j], coinSum + dp[i-1][j - c - 1]); 
                }
            }
        }

        return dp[numPiles][k];
    }
}
