## **Top Down Approach**

*Pure top down accepted solution but less efficient*

```
class Solution {
public:
    int helper(vector<vector<int>> &piles, int n, int k, int i, int index[], vector<vector<vector<int>>> &dp) {
        if(i == n || k == 0) return 0;

        if(dp[i][index[i]][k] != -1) return dp[i][index[i]][k];

        int x = 0, y = 0;
        if(index[i] < piles[i].size()) {
            index[i]++;
            x = piles[i][index[i]-1] + helper(piles, n, k-1, i, index, dp);
            index[i]--;
        }
        y = helper(piles, n, k, i+1, index, dp);

        return dp[i][index[i]][k] = max(x, y);    
    }

    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n = piles.size();

        int index[1000] = {0};
        vector<vector<vector<int>>> dp(n);
        for(int i=0; i<n; i++) {
            vector<vector<int>> temp(piles[i].size()+1, vector<int>(k+1, -1));
            dp[i] = temp;
        }

        return helper(piles, n, k, 0, index, dp);
    }
};
