 int maxValueOfCoins(vector<vector<int>>& p, int k) {
        int n=p.size();
        vector<vector<int>> dp(n+1,vector<int>(k+1,0));

        for(int i=n-1;i>=0;--i){
            for(int j=0;j<=k;++j){
                int nt=dp[i+1][j];
                int a=0;
                 for(int m=0;m<p[i].size()&&m<j;++m){
                     a+=p[i][m];
                     nt=max(nt,a+dp[i+1][j-m-1]);
                 }
                dp[i][j]=nt;
                }
        }
        return dp[0][k];
    }


	// T.C.=> O(n*k^2)
	// S.C=> O(n*k)


