class Solution {
public:
    long long waysToBuyPensPencils(int total, int cost1, int cost2) {
        vector<int> v;
        v.push_back(cost1);
        v.push_back(cost2);

        int m = v.size();
        int n = total;

        vector<long long> prev(n+1,0),cur(n+1,0);

        for(int i = 0;i < m;i++) prev[0] = 0;

        for(int T = 1; T <= n;T++) prev[T] = T/v[0];

        for(int i = 1; i< m; i++){
            for(int T = 1; T <= n;T++){
                long long nottake = prev[T];

                long long take = 0;

                if(T >= v[i]) take = 1 + cur[T - v[i]];

                cur[T] =  take + nottake;
            }
            prev = cur;
        }

        return prev[n] + 1;
    }
};
