class Solution {
public:

    typedef long long ll;


    long long waysToBuyPensPencils(int n, int x, int y) {

        vector<ll> dp(n+1,1);

        dp[0] = 1;

        for(int i = 1; i < n+1; i++){

            if(i >= x)
                dp[i] += dp[i-x];

            if(i >= y)
                dp[i] += dp[i-y];

            if(i >= x+y)
                dp[i] -= dp[i-x-y];

        }
        return dp[n];
    }
};

