class Solution {
public:
    long long waysToBuyPensPencils(int total, int cost1, int cost2) 
    {
        int pen=max(cost1,cost2);
        int pencil=min(cost1,cost2);
        int mx_pens=total/pen;
        long long ctr=0;
        for(int i=0;i<=mx_pens;i++)
        {
            int left=total-(i*pen);
            int no_pencil=left/pencil;
            ctr+=(long long)no_pencil+1;
        }
    return ctr;    
    }
};
```

