class Solution {
public:

    long long max(long long x , long long y)
    {
        if(x > y) return x; 
        return y;
    }

    long long waysToBuyPensPencils(int n, int c1, int c2) {
        long long ans = 0; 
        // c1*x + c2*y = n
        int max_x = n / c1; 
        for(int i = 0 ; i <= max_x ; ++i){
            int n_y = n - (i * c1); 
            if(n_y < 0) break; 
            int cnt = n_y / c2; 
            int rem = n_y % c2; 
            ++cnt; 
            //cout << " i " << i << " ny " << n_y << " rem " << rem << " cnt " << cnt << endl; 
            ans = ans + cnt; 
        }
        return max(1, ans); 
    }
};
```

