class Solution {
public:
    long long waysToBuyPensPencils(int total, int cost1, int cost2) {
        if(cost1 > total && cost2 > total) return 1;
        if(cost1 < cost2) swap(cost1 , cost2);
        long long x = 0, res = 0;
        while(total > 0){
            total -= x;
            if(total >= 0)
                res += total/cost2 + 1;
            x = cost1;
        }
        return res;
    }
};
```

