class Solution {
public:
    int usingSlidingWindow(vector<int> &cards){
		// Stores the card and its index
        unordered_map<int,int> um;
        int ans = INT_MAX;

        int i=0, j=0;
        while(j<cards.size()){
            int card = cards[j];
            // If card already present, slide the window until the card is found
            while(um.find(card) != um.end() && i<j){
                ans = min(ans, j-i+1);
                um.erase(cards[i]);
                i++;
            }
            um[card] = j;
            j++;
        }

        return ans == INT_MAX ? -1 : ans;
    }
};
```
