class Solution {
public:
    int minimumCardPickup(vector<int>& arr) {

        int n = arr.size();

        unordered_map<int, int> mp;

        int mini = INT_MAX;

        int left = 0;

        for(int right = 0; right < n; right++)
        {
            mp[arr[right]]++;

            while(left <= right && mp[arr[right]] > 1)
            {
                mini = min(mini, right - left + 1);

                mp[arr[left]]--;

                left++;
            }
        }

        return mini == INT_MAX ? -1 : mini;
    }
};
