class Solution {
public:
    int minimumCardPickup(vector<int>& cards) {
        int n = cards.size();

        int i = 0,j = 0,mn = INT32_MAX;
        unordered_map<int,int>mp;

        // SIMPLE SLIDING WINDOW PROBLEM

        while(j < n)
        {
            mp[cards[j]]++;

            if(j-i+1 == mp.size())
            {
                j++;
                // Normal procedure if unique characters are coming, keep inserting into map
            }
            else if(j-i+1 > mp.size())
            {
                // When a duplicate character comes length(or size of the sliding window)
                // becomes greater than the map's size
                while(j-i+1 > mp.size())
                {
                    mp[cards[i]]--;
                    if(mp[cards[i]] == 0)
                    {
                        mp.erase(cards[i]);
                    }
                    mn =  min(mn,j-i+1);
                    i++;
                }

                // HERE INCREMENTING J IS IMPORTANT because we don't want to insert previous 
                // cards[j] into the map again
                j++;
            }
        }

        if(mn == INT32_MAX) return -1;

        return mn;
    }
};

