class Solution {
public:
    int minimumCardPickup(vector<int>& a) {
        int n=a.size();
        map<int,int>m;
        int l=0, r=0, ans=INT_MAX;
        while(r<n)
        {
            m[a[r]]++;
            while(m[a[r]]>1)
            {
                ans= min(ans, r-l+1);
                m[a[l]]--;
                l++;
            }
            r++;
        }
        return ans!=INT_MAX ? ans:-1;
    }
};
